  margin-top: 3rem;
}
.about__contentContainer__img {
  position: relative;
  margin-left: 5rem;
  grid-area: img;
  height: 30rem;
  background: url(/d63b654a039d1d4e3b88e08816acc19f.png);
  background-repeat: no-repeat;
  transition: 0.3s;
}
.about__contentContainer__img::before, .about__contentContainer__img::after {
  position: absolute;
