  width: 35%;
  height: 32%;
}
.about__contentContainer__p {
  grid-area: p;
  margin-right: 5rem;
  font-size: 2.2rem;
  font-weight: 300;
  line-height: 5.5rem;
  color: #1a5f7a;
}
.about__contentContainer__p span {
  color: #ffc900;
}

