@use "normalize.scss";
@use "arrowDown.scss";

$yellow: #ffc900;
$navyBlue: #1a5f7a;
$darkNavyBlue: #124154;

$regular: 300;
$medium: 400;
$bold: 500;
$extraBold: 600;

$regularSize: 2.2rem;
$logoSize: 3.4rem;
$headerSize: 7.2rem;
$secondaryHeadersSize: 6.8rem;
$tertiaryHeadersSize: 3.4rem;
$listSize: 3.4rem;

body {
  background-image: url("../assets/background.png");
  background-size: 22rem;
  font-family: "Poppins", sans-serif;
}

.gearsImageContainer {
  width: 50vw;
  //height: 70vh;
  z-index: -1;
  position: absolute;
  top: calc(100vh / 2);
  left: 50%;
